const int MOD = 1e9+7;
class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {

        vector<int> require(n,-1);
        for(auto v : requirements) {
            int need = v[1];
            int where = v[0];
            require[where]=need;
        }
        const int MX = require[n-1];
        vector<int> dp(MX+1);
        dp[0]=1;
        for(int i=0;i<n;++i) {
            // cout << i << endl;
            vector<int> ndp(MX+1);
            for(int at=0;at<=MX;++at) if(dp[at])
            for(int num=0;num<=i;++num) {
                if(at+num<=MX and (require[i]==-1 or at+num==require[i]) ) {
                    ndp[at+num]+=dp[at];
                    if(ndp[at+num]>=MOD) ndp[at+num]-=MOD;
                }
            }
            swap(dp,ndp);
        }
        return dp[require[n-1]];
    }
};
