const int mod = 1000000007;

int d[333][444];

class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& r) {
        SORT(r);
        CL(d,0);

        d[0][0] = 1;
        int limit = r.back()[1];
        int p = 0;
        REP(i,n){
            while(r[p][0]<i)p++;
            int need=-1;
            if(r[p][0]==i)need=r[p][1];
            REP(j,limit+1)if(d[i][j]){
                REP(k,i+1)if(j+k<=limit && (need==-1 || j+k==need)){
                    d[i+1][j+k] += d[i][j];
                    if(d[i+1][j+k]>=mod)d[i+1][j+k]-=mod;
                }
            }
        }

        return d[n][r.back()[1]];
    }
};
