import java.util.HashMap;
import java.util.Map;

public class Solution {
    private static final int MOD = 1_000_000_007;
    private static final int MAX_INVERSIONS = 400;

    public int numberOfPermutations(int n, int[][] requirements) {
        Map<Integer, Integer> endIdxToInversionCnt = new HashMap<>();
        for (int[] req : requirements) {
            endIdxToInversionCnt.put(req[0] + 1, req[1]);
        }

        long[][] dp = new long[n + 1][MAX_INVERSIONS + 1];
        dp[0][0] = 1;

        for (int length = 1; length <= n; length++) {
            for (int currentInversions = 0; currentInversions <= MAX_INVERSIONS; currentInversions++) {
                for (int newPosition = 0; newPosition < length; newPosition++) {
                    int previousInversions = currentInversions - newPosition;
                    if (previousInversions >= 0) {
                        dp[length][currentInversions] = (dp[length][currentInversions] + dp[length - 1][previousInversions]) % MOD;
                    }
                }
            }

            if (endIdxToInversionCnt.containsKey(length)) {
                int requiredInversions = endIdxToInversionCnt.get(length);
                for (int currentInversions = 0; currentInversions <= MAX_INVERSIONS; currentInversions++) {
                    if (currentInversions != requiredInversions) {
                        dp[length][currentInversions] = 0;
                    }
                }
            }
        }

        long result = 0;
        for (int currentInversions = 0; currentInversions <= MAX_INVERSIONS; currentInversions++) {
            result = (result + dp[n][currentInversions]) % MOD;
        }

        return (int) result;
    }
}
