#define ll long long int
const int mod = 1e9 + 7;
class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        map<ll, ll> mp;
        for(auto r : requirements) mp[r[0] + 1] = r[1];

        vector<vector<ll>> dp(n + 1, vector<ll>(401, 0));
        dp[0][0] = 1;

        for(int i = 1; i <= n; i++) {
            for(int j = 0; j <= 400; j++) {
                for(int k = 0; k < i; k++) {
                    int temp = j - k;
                    if(temp >= 0)
                        dp[i][j] = (dp[i][j] + dp[i - 1][temp]) % mod;
                }
            }

            if(mp.count(i)) {
                int ttemp = mp[i];
                for(int p = 0; p <= 400; p++) {
                    if(p != ttemp)  dp[i][p] = 0;
                }
            }
        }

        ll ans = 0;
        for(auto x : dp[n]) ans = (ans + x) % mod;
        return ans;
    }
};
