#define ll long long
class Solution {
public:
    const int mod=1e9+7;
    const int cnt=400;
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        int m=requirements.size();
        unordered_map<ll,ll> mp;
        ll ans=0;
        for(int i=0;i<m;i++){
            mp[requirements[i][0]+1]=requirements[i][1];
        }
        vector<vector<ll>> dp(n+1,vector<ll>(cnt+1,0));
        dp[0][0]=1;
        for(int i=1;i<=n;i++){
            for(int j=0;j<=cnt;j++){
                for(int k=0;k<i;k++){
                    if(j-k>=0){
                        dp[i][j]=(dp[i][j]+dp[i-1][j-k])%mod;
                    }
                }
            }
            if(mp.find(i)!=mp.end()){
                for(int j=0;j<=cnt;j++){
                    if(j!=mp[i]){
                        dp[i][j]=0;
                    }
                }
            }
        }
        for(int i=0;i<=cnt;i++){
            ans=(ans+dp[n][i])%mod;
        }
        return ans;
    }
};
