from typing import List

class Solution:
    def numberOfPermutations(self, n: int, require: List[List[int]]) -> int:
        mo=1000000007
        req={}
        noe=400
        ans=0
        for i in require:req[i[0]+1]=i[1]
        v=[[0]*(401) for _ in range(n + 1)]
        v[0][0] = 1
        for i in range(1, n+1):
            for j in range(401):
                for k in range(i):
                    pj=j-k
                    if pj>=0:v[i][j]=(v[i][j]+v[i-1][pj])%mo
            if i in req:
                tj=req[i]
                for l in range(401):
                    if l!=tj:v[i][l]=0
        for i in v[n]:ans=(ans+i)%mo
        return ans
