class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& req) {
        const long long  mod=1e9+7;
        long long dp[n+2][405];
        memset(dp,0,sizeof(dp));
        dp[0][0]=1;


        map<int,int>mp;
        for(auto it:req){
            mp[it[0]+1]=it[1];
        }
        for(int i=1;i<=n;i++)
        {
            for(int j=0;j<=400;j++)
            {
                for(int k=0;k<i;k++)
                {
                    int xx=j-k;
                    if(xx>=0)
                    {
                        dp[i][j]=(dp[i][j]+dp[i-1][xx])%mod;
                    }
                }
            }
            if(mp.count(i)){
                int xx=mp[i];
                for(int j=0;j<=400;j++)
                {
                    if(j!=xx)dp[i][j]=0;
                }
            }
        }

        int ans=0;
        for(int j=0;j<=400;j++){
            ans+=dp[n][j];
            ans%=mod;
        }
        return ans;
    }
};
