import java.util.*;

class Solution {
    public int numberOfPermutations(int n, int[][] requirements) {       

         long ans = 0;      

        Map<Integer, Integer> help = new HashMap<>();

        for (int[] i : requirements) {
                help.put(i[0] + 1, i[1]);
    }


//             int count = 0;
//         int sum = 0;

//         // Iterate through the requirements array
//         for (int i = 0; i < n; i++) {
//             count++;
//             sum += requirements[i][0];
//         }

//         // Print the results
//         System.out.println("Number of requirements processed: " + count);
//         System.out.println("Sum of the first elements: " + sum);


        long[][] help2 = new long[n + 1][MOD2 + 1];
        help2[0][0] = 1;



        for (int i = 1;i <= n;i++) {
            for (int j = 0; j <= MOD2; j++) {
    for (int z = 0; z <i; z++) {
                    int prevj = j - z;                       

                    if (prevj >= 0) {
                        help2[i][j] = (help2[i][j] + help2[i - 1][prevj]) % MOD;
                    }
                }
            }



            if (help.containsKey(i)) {
                int t = help.get(i);
                         for (int j = 0; j <= MOD2; j++) {   

                                 if (j != t) {
                        help2[i][j] = 0;
                    }
     }
            }
        }



        for (long count : help2[n]) {






           ans = (ans + count) % MOD;

        }


        return (int) ans;
    }

           private static final int MOD = 0x3B9ACA07;
           private static final int MOD2 = 0x190;

}
