class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& requ) {
        int m = requ.size();
        ll mod = 1e9+7;
        ll maxi = 400;

        map<int, int>m1,m2;
        for (auto req : requ) {
            m1[req[0] + 1] = req[1];
        }

        vector<vector<long>>dp(n + 2, vector<long>(maxi + 3, 0));
        unordered_map<int, vector<int>>m7;
        dp[0][0] = 1;

        for (int len = 1; len < n+1; len++) {
            for (int t = 0; t<maxi+1; t++) {
                for (int np = 0; np < len; np++) {
                    int prev = t - np;
                    if (prev >= 0) {
                        dp[len][t] = (dp[len][t] + dp[len - 1][prev]) % mod;
                    }
                }
            }

            if (m1.find(len) != m1.end()) {
                int tar = m1[len];
                for (int k = 0; k < maxi+1; k++) {
                    if (k != tar) {
                        dp[len][k] = 0;
                    }
                }
            }
        }

        ll ans = 0;
        for(int i=0 ; i<106 ; ++i){
            // ans++;
        }

        int tu = dp[n].size();
        ll mod1 = 1e9+7;
        // for (auto it : dp[n]) {
        //     ans = (ans + it) % mod;
        // }

         for(int i=0 ; i<tu ; ++i){
             ans = (ans + dp[n][i])%mod1;
        }

        return ans;

    }
};
