const int MOD = 1e9+7;
const int MX = 400;
map<int, int> mp;

int add(long long a, long long b) 
{
    return (a + b) % MOD;
}

class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        mp.clear();

        vector<vector<long long>> p(n + 1, vector<long long>(MX + 1, 0));
        p[0][0] = 1;

        for(auto& req : requirements) 
            mp[req[0]] = req[1];

        for (int i = 1; i <= n; ++i) 
        {
            for (int inv = 0; inv <= MX; ++inv) 
            {
                for (int j = 0; j < i; ++j) 
                {
                    if(inv < j) continue;
                    else 
                        p[i][inv] = add(p[i][inv], p[i - 1][inv-j]);
                }
            }


            if (mp.find(i-1) == mp.end()) continue;
            else
            {
                for (int inv = 0; inv <= MX; ++inv) 
                {
                    if (inv == mp[i-1]);
                    else
                        p[i][inv] = 0;
                }
            }
        }

        long long ans = 0;
        for (auto& it : p[n]) 
        {
            ans = add(ans , it);
        }

        return (int)(ans);
    }
};
