#include <vector>
#include <unordered_map>
using namespace std;

class Solution {
public:
     int numberOfPermutations(int n, vector<vector<int>>& requirements) {
       unordered_map<int, int> reqMap;
        for (auto& req:requirements)
            reqMap[req[0] + 1] = req[1]; 
        vector<vector<long long>>vect(n+1,vector<long long>(400+1,0));
        vect[0][0]=1; 
        for (int i=1;i<=n;i++) {
            for (int inv=0; inv <= 400; inv++) {
                for (int newp=0; newp < i; newp++) {
                    int pre=inv-newp;
                    if (pre>=0) {
                        vect[i][inv]=(vect[i][inv]+vect[i-1][pre])%1000000007;
                    }
                } if (reqMap.count(i)) {
                    int tempp=reqMap[i];
                    if (inv!=tempp)
                        vect[i][inv]=0;
                }
            }
        }  long long res = 0;
        for (int inv = 0; inv <= 400; inv++)res=(res+vect[n][inv])%1000000007;

        return res;
    }
};
