class Solution:
    MOD = 1000000007
    MAX_INVERSIONS = 400

    def __init__(self):
        pass

    def numberOfPermutations(self, n, requirements):
        req_map = {}
        for req in requirements:
            req_map[req[0] + 1] = req[1]

        perm_counts = [[0] * (self.MAX_INVERSIONS + 1) for _ in range(n + 1)]
        perm_counts[0][0] = 1

        for length in range(1, n + 1):
            for inv in range(self.MAX_INVERSIONS + 1):
                for new_pos in range(length):
                    prev_inv = inv - new_pos
                    if prev_inv >= 0:
                        perm_counts[length][inv] = (perm_counts[length][inv] + perm_counts[length - 1][prev_inv]) % self.MOD

            if length in req_map:
                target_inv = req_map[length]
                for inv in range(self.MAX_INVERSIONS + 1):
                    if inv != target_inv:
                        perm_counts[length][inv] = 0

        result = sum(perm_counts[n]) % self.MOD
        return result
