#define ll long long
class Solution {
public:
    ll MOD = 1e9+7;
    ll f(int n, int S, int diff) {
        vector<ll> dp(S + 1, 0);
        vector<int>v(n+1,0);
        for(int i=n;i>diff;i--){
            v[i]=i;
        }
        dp[0] = 1;
        for (int i=1;i<=n;i++) {
            for (int j = S; j >= 0; j--) {
                for (int k = 1; k <= v[i]; k++) {
                    if (j - k >= 0) {
                        dp[j] = (dp[j] + dp[j - k])%MOD;
                    }
                }
            }
        }
        return dp[S];
    }

    static bool comp(vector<int>&a, vector<int>&b){
        return a[0]<b[0];
    }

    int numberOfPermutations(int n, vector<vector<int>>& r) {
        int m = r.size();
        if(m>1)sort(r.begin(),r.end(),comp);
        ll ans=1;
        int prev=0;
        int cnt=0;
        for(int i=0;i<m;i++){
            ll val=f(r[i][0],r[i][1]-prev,cnt);
            ans = (ans*val)%MOD;
            prev=r[i][1];
            cnt=r[i][0];
        }
        return ans;
    }
};
