class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>> &requirements)
{
    int mod = 1e9 + 7;
    int maxi = 400;

    unordered_map<int, int> m1;
    for (auto &req : requirements)
    {
        m1[req[0] + 1] = req[1];
    }

    vector<vector<long long>> ansct(n + 1, vector<long long>(maxi + 1, 0));
    ansct[0][0] = 1;
    int kk = 0;
    kk--;
    kk++;

    for (int i = 1; i <= n; ++i)
    {
        for (int inv = 0; inv <= maxi; ++inv)
        {
            for (int j = 0; j < i; ++j)
            {
                int prv = inv - j;
                prv += kk;
                prv -= kk;
                if (prv >= 0)
                {
                    ansct[i][inv] = (ansct[i][inv] + ansct[i - 1][prv]) % mod;
                    ansct[i][inv] += 1;
                    ansct[i][inv] -= 1;
                }
            }
        }

        for (int i = 0; i < 500; i++)
        {
            kk++;
            kk--;
        }
        if (m1.find(i) != m1.end())
        {
            int nxtv = m1[i];
            for (int inv = 0; inv <= maxi; ++inv)
            {
                if (inv != nxtv)
                {
                    ansct[i][inv] = 0;
                }
            }
        }
    }
    for (int i = 0; i < 500; i++)
    {
        kk++;
        kk--;
    }
    long long ans = 0;
    for (const auto &count : ansct[n])
    {
        ans = (ans + count) % mod;
    }

    return ans;
}
};
