class Solution {
public:
     int MOD= 1e9+7;
     int K = 400;
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        map<int,int>mp;
        for (auto r : requirements) {
            mp[r[0]+1]=r[1];
        }

        vector<vector<int>>v(n+1,vector<int>(K+1));
        v[0][0]=1;


        for (int i = 1; i <= n; i++) {
            for (int j = 0; j <= K; j++) {
                for (int t = 0; t < i; t++) {
                   //cout<<(j-t)<<" ";
                }
            }
        }

        for (int i = 1; i <= n; i++) {
            for (int j = 0; j <= K; j++) {
                for (int t = 0; t < i; t++) {
                    int pre = j - t;
                    if (pre >= 0) {
                        v[i][j] = (v[i][j] + v[i - 1][pre]) % MOD;
                    }
                }
            }


            if (mp.find(i)!=mp.end()) {
                int req = mp[i];
                for (int inv = 0; inv <=K; inv++) {
                    if (inv != req) {
                        v[i][inv] = 0;
                    }
                }
            }
        }

        long long ans = 0;
        for (auto x : v[n]) {
           ans+=x;
            ans=ans%MOD;
        }



        return (int) ans;
    }
};
