#define ll long long int
class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        map<int,int> m;
        for (auto v:requirements) {
            m[v[0]+1] = v[1];
        }
        vector<vector<ll>> v1(n+1,vector<ll>(401,0));
        v1[0][0] = 1;
        for (int i=1; i<=n; i++){
            for (int j=0; j<=400; j++) {
                for (int k=0; k<i; k++) {
                    int p=j-k;
                    if (p>=0) {
                        v1[i][j] = (v1[i][j]+v1[i-1][p])%(1000000007);
                    }
                }
            }
            if (m.find(i)!=m.end()) {
                int c = m[i];
                for (int j=0; j<=400; j++) {
                    if (j!=c) {
                        v1[i][j] = 0;
                    }
                }
            }
        }
        long long ans = 0;
        for (auto x:v1[n]) {
            ans=(ans+x)%(1000000007);
        }
        return ans;
    }
};
