#include <bits/stdc++.h>
using  namespace std;
int MOD = 1e9+7;

class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& r) {
        ios_base::sync_with_stdio(false);
        cin.tie(NULL);
        cout.tie(NULL);
        unordered_map<int, int> mp;
        long tot = 0;
        for (auto it : r)
            mp[it[0]+1] = it[1];

        // 4 3 2 1
        // cnt 6

        vector<vector<long>> PC(n + 1, vector<long>(400 + 1, 0));
        PC[0][0] = 1;
        for (int i = 1; i <= n; ++i) {
            for (int j = 0; j <= 400; ++j)
                for (int k = 0; k < i; ++k) 
                    if (j-k >= 0)
                        PC[i][j] = (PC[i][j] + PC[i - 1][j-k]) % MOD;

            if (mp.find(i) != mp.end()) {
                int tar = mp[i];
                for (int j = 0; j <= 400; ++j)
                    if (j != tar)
                        PC[i][j] = 0;
            }
        }
        // 2 0 1
        // permcnt 2,2

        for (long it : PC[n])
            tot = (tot + it) % MOD;
        return static_cast<int>(tot);
    }
};
