#define ll long long 
int mod = 1000000007;
class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& r) {
        map<int, int> m;
        for (auto t : r) {
            m[t[0] + 1] = t[1];
        }

        vector<vector<ll>> dp(n + 1, vector<ll>(401, 0));
        dp[0][0] = 1;

        for (int l = 1; l <= n; l++) {
            for (int i = 0; i <= 400; i++) {
                for (int p = 0; p < l; p++) {
                    int k = i - p;
                    if (k >= 0) {
                        dp[l][i] = (dp[l][i] + dp[l - 1][k]) % mod;
                    }
                }
            }

            if (m.count(l)) {
                int t = m[l];
                for (int i = 0; i <= 400; i++) {
                    if (i != t) {
                        dp[l][i] = 0;
                    }
                }
            }
        }
    // for(int i = 0 ; i<= n  ; i++) ans+= dp[i];
        ll ans = 0;
        for ( auto c : dp[n]) {
            ans = (ans + c) % mod;
        }

        return ans;
    }
};
