class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& r) {
        int mod=1e9+7;
        map<int,int>mp;
        for(auto x:r){
            mp[x[0]+1]=x[1];
        }
        vector<vector<int>>a(n+1,vector<int>(401));
        a[0][0]=1;
        for(int i=1;i<=n;i++){
            for(int j=0;j<=400;j++){
                for(int k=0;k<i;k++){
                    int cnt=j-k;
                    if(cnt>-1){
                        a[i][j]+=(a[i-1][cnt]);
                        a[i][j]%=mod;
                    }
                }
            }
            if(mp.find(i)==mp.end()) continue;
            else{
                int f=mp[i];
                for(int sha=0;sha<=400;sha++){
                    if(sha!=f){
                        a[i][sha]=0;
                    }
                }
            }
        }
        int ans=0;
        for(auto x:a[n]){
            ans+=x;
            ans%=mod;
        }
        return ans;
    }
};
