class Solution:
    def numberOfPermutations(self, n: int, requirements: List[List[int]]) -> int:
        M = 10**9+7
        maxi = 400
        mapi={}
        for i in requirements:
            mapi[i[0]+1]=i[1]
        dp=[[0]*(maxi+1) for _ in range(n+1)]
        dp[0][0]=1
        for i in range(1,n+1):
            for cnt in range(maxi+1):
                for j in range(i):
                    if cnt-j>=0:
                        dp[i][cnt]=(dp[i][cnt]+dp[i-1][cnt-j])%M
            if i in mapi:
                t = mapi[i]
                for cnt in range(maxi+1):
                    if cnt!=t:
                        dp[i][cnt]=0
        res = 0
        for cnt in dp[n]:
            res=(res+cnt)%M
        return res
