#define MAXIM 400

class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {



        vector<vector<int>> dp(n + 1,vector<int>(MAXIM + 1, 0));
        dp[0][0] = 1;

        // Process requirements
        vector<int> arr(n, -1);
        for (const auto& req : requirements) {
            arr[req[0]] = req[1];
        }

        // Fill DP table
        Rep(len,1,n+1) {
            for (int inv = 0; inv <= MAXIM; ++inv) {
                for (int k = 0; k < len; ++k) {
                    if (inv >= k) {
                        dp[len][inv] = (dp[len][inv] + dp[len - 1][inv - k]) % MOD;
                    }
                }
            }

            if (arr[len - 1] != -1) {
                int needf = arr[len - 1];
                for (int inv = 0; inv <= MAXIM; ++inv) {
                    if (inv != needf) {
                        dp[len][inv] = 0;
                    }
                }
            }

        }
        return dp[n][arr[n - 1]];
    }
};
