class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>&r) {
        const int MOD = 1000000007;
        const int MAX = 400;
        unordered_map<int,int>mp;
        for(auto& it : r)mp[it[0]+1] =it[1];
        vector<vector<long long>>dp(n+1,vector<long long>(MAX+1,0));
        dp[0][0] =1;
        for(int i=1;i<=n;i++){
            for(int j=0;j<=MAX;j++){
                for(int k=0;k<i;k++){
                    if(j-k>=0){
                        dp[i][j]=(dp[i][j]+dp[i-1][j-k])%MOD;
                    }
                }
            }
            if(mp.find(i)!=mp.end()){
                int inv=mp[i];
                for(int j=0;j<=MAX;j++){
                    if(j!=inv)dp[i][j] =0;
                }
            }
        }
        long long res =0;
        for(long long cnt : dp[n])res = (res+cnt)%MOD;
        return res;
    }
};
