class Solution {
    int mod = 1e9 + 7;
public:
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        unordered_map<int, int> mp;
        for (auto &it : requirements)
            mp[it[0] + 1] = it[1];
        vector<vector<long long>> cnt(n + 1, vector<long long>(401, 0));
        cnt[0][0] = 1;

        for (int i = 1; i <= n; i++) {
            for (int j = 0; j <= 400; j++) {
                for (int p = 0; p < i; p++) {
                    if (j - p >= 0) {
                        cnt[i][j] = (cnt[i][j] + cnt[i - 1][j - p]) % mod;
                    }
                }
            }
            if (mp.count(i)) {
                for (int j = 0; j <= 400; j++) {
                    if (j != mp[i]) {
                        cnt[i][j] = 0;
                    }
                }
            }
        }
        int val = 0;
        for (int i = 0; i < 401; i++)
            val = (1LL * val + cnt[n][i]) % mod;

        return val;
    }
};
