class Solution {
public:
    int numberOfPermutations(int a, vector<vector<int>>& b) {
        using ll = long long;


        const int MOD = 1e9 + 7;


        const int MAXI_MAX = 400;


        map<int, int> c;
        sort(begin(b), end(b));
        for (auto& d : b) {
            c[d[0] + 1] = d[1];
        }
        vector<vector<long>> e(a + 1, vector<long>(MAXI_MAX + 1, 0));
        e[0][0] = 1;

        for (int f = 1; f <= a; f++) {
            for (int g = 0; g <= MAXI_MAX; g++) {
                for (int h = 0; h < f; h++) {
                    int i = g - h;
                    if (i >= 0) {
                        e[f][g] = (e[f][g] + e[f - 1][i]) % MOD;
                    }
                }
            }


            if (c.count(f)) {
                int j = c[f];
                for (int g = 0; g <= MAXI_MAX; g++) {
                    if (g != j) {
                        e[f][g] = 0;
                    }
                }
            }
        }
        long k = 0;
        for (long l : e[a]) {
            k = (k + l) % MOD;
        }
        return k;
    }
};
