class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& vv) {
        map<int,int> mp;
        int mod=1e9+7;
        for(int i=0;i<vv.size();i++){
            mp[vv[i][0]+1]=vv[i][1];
        }
        vector<vector<int>> dp(n+1,vector<int> (501));
        dp[0][0]=1;
        for(int i=1;i<=n;i++){
            for(int j=0;j<=500;j++){
                for(int k=0;k<i;k++){
                    if(j>=k) dp[i][j]=(dp[i][j]%mod+dp[i-1][j-k]%mod)%mod;
                }
            }
            auto fnd=mp.find(i);
            if(fnd!=mp.end()){
                for(int temp=0;temp<=500;temp++){
                    if(temp!=fnd->second) dp[i][temp]=0;
                }
            }
        }
        long ans=0;
        for(auto it:dp[n]) ans=(ans+it)%mod;
        return ans;

    }
};
