#define ll long long int
int mod=1e9+7;
class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        sort(requirements.begin(),requirements.end());
        int n2=requirements.size();
        int mi=400;
        map<ll,ll>m1;
        for(auto i:requirements){
            m1[i[0]+1]=i[1];
        }
        vector<vector<ll>>p1(n+1,vector<ll>(mi+1,0));
        p1[0][0]=1;
        for(int i=1;i<=n;i++){
            for(int j=0;j<=mi;j++){
                for(int k=0;k<i;k++){
                    ll p3=j-k;
                    if(p3>=0){
                        p1[i][j]=p1[i][j]+p1[i-1][p3]%mod;
                    }
                }
            }
            if(m1.find(i)!=m1.end()){
                int tar=m1[i];
                for(int k5=0;k5<=mi;k5++){
                    if(k5!=tar){
                        p1[i][k5]=0;
                    }
                }
            }
        }
        long long ans=0;
        for(auto k2:p1[n]){
            ans=(ans+k2)%mod;
        }
        return ans;

    }
};
