int mod = 1e9+7;
typedef long long ll;
class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& requirements) 
    {
        unordered_map<int,int>mp;
        vector<vector<ll>> arr(n +1,vector<ll>(401,0));
        arr[0][0]=1;
        for (auto i :requirements ) 
        {
            mp[i[0]+1] =i[1];
        }
        for (int i =1;i <=n;i++) 
        {
            for (int j =0;j <=400;j++) 
            {
                for (int k =0;k < i;k++) 
                {
                    int temp = j - k;
                    if (temp >= 0) 
                    {
                        arr[i][j] = (arr[i][j]+arr[i-1][temp])%mod;
                    }
                }
            }
            if (mp.find(i)!=mp.end()) 
            {
                int target =mp[i];
                for (int j = 0;j<=400;j++) 
                {
                    if (j!=target) 
                    {
                        arr[i][j] =0;
                    }
                }
            }
        }
        ll ans = 0;
        for (int i=0;i<=400;i++) 
        {
            ans = (ans+arr[n][i])%mod;
        }
        int res =ans%mod;
        return res;
    }
};
