class Solution {

public:
    int mod = 1000000007;

    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        int maxi = 400;
        map<int, int> mp;
        vector<vector<int>> perm(n + 1, vector<int>(maxi + 1, 0));
        perm[0][0] = 1;
        for (auto it:requirements) {
            mp[++it[0]] = it[1];
        }

        for (int i=1; i <= n; i++) {
            for (int j = 0; j <= maxi; j++) {
                for (int k=0; k<i; k++) {
                    int prev = j - k;
                    if(prev<0)continue;
                    perm[i][j] = (perm[i][j] + perm[i - 1][prev]) % mod;    
                    perm[i][j]=perm[i][j]%mod;
                }
            }

            if (mp.count(i)) {
                int target = mp[i];
                for (int j =0; j<= maxi; j++) {
                    if (j!=target) {
                        perm[i][j] = 0;
                    }
                }
            }
        }

        int result = 0;
        for (auto it:perm[n]) {
            result =(result + it)%mod;
        }
        return (result);
    }
};
