class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        vector<int> chk(n);
        for (int i = 0; i < n; i ++) {
            chk[i] = (
                colors[i] != colors[(i + n - 1) % n]
                && colors[i] != colors[(i + 1) % n]
            );
        }
        for (int i = 0; i < n; i ++)
            chk.push_back(chk[i]);
        int ans = 0, tmp = 0;
        for (int i = 1; i < k - 1; i ++)
            tmp += chk[i];
        for (int i = 0; i < n; i ++) {
            ans += tmp == k - 2;
            if (i != n - 1) {
                tmp -= chk[i + 1];
                tmp += chk[i + k - 1];
            }
        }
        return ans;
    }
};
