int sum[210000];

class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int i,j,n,ans;
        for (i=0;i+1<k;i++)
            a.push_back(a[i]);
        n=a.size();
        sum[0]=0;
        for (i=0;i+1<n;i++)
        {
            sum[i+1]=sum[i];
            if (a[i]!=a[i+1]) sum[i+1]++;
        }
        ans=0;
        for (i=0;i+k-1<n;i++)
        {
            if (sum[i+k-1]-sum[i]==k-1) ans++;
        }
        return ans;
    }
};
