class Solution {
public:
  int numberOfAlternatingGroups(vector<int>& colors, int k) {
    std::vector<int> a;
    int n = colors.size();
    for (int i = 0; i < n * 2; i++) {
      a.push_back(colors[i % n]);
    }
    std::queue<int> q;
    for (int i = 0; i < k - 1; i++) {
      if (a[i] == a[i + 1]) {
        q.push(i);
      }
    }
    int ans = 0;
    for (int i = 0; i < n; i++) {
      if (q.empty()) {
        // std::cout << i << ' ';
        ans++;
      }
      if (!q.empty() && q.front() == i) {
        q.pop();
      }
      if (a[i + k - 1] == a[i + k]) {
        q.push(i + k - 1);
      }
    }
    // std::cout << '\n';
    return ans;
  }
};

