class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& col, int k) {
        col.insert(col.end(),col.begin(),col.begin()+k-1);

        int ans=0, n=1;

        for(int i=1; i<col.size(); i++){
            n=(col[i]!= col[i-1])?n+1:1;
            if(n>=k){
                ans++;
            }
        }
        return ans;
    }
};
