class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();

        for(int i=0;i<n;i++)
            colors.push_back(colors[i]);
        int ans = 0;
        int i = 0;
        int alter = 0;
        for(i=0;i<k-2;i++)
            if(colors[i] != colors[i+1])
                alter++;
            else
                alter = 0;
       // cout<<"i-k = "<<i-k<<endl;
        while(i-k < n-2)
        {
            if(colors[i] != colors[i+1])
                alter++;
            else
                alter = 0;
            if(alter >= k-1)
            {
                ans++;
               // cout<<i-k<<" ";
            }
            i++;
        }
        return ans;
    }
};
