/**
 * @param {number[]} colors
 * @param {number} k
 * @return {number}
 */
var numberOfAlternatingGroups = function(colors, k) {
    let preColor = colors[0]
    let currCount = 1
    let rs = 0 
        if(currCount >=k)
                {
                    rs++
                }

    for(let i = 1; i < colors.length + k -1 ; i++ )
        {

             let temp = i % colors.length
             let currColor = colors[temp]
            if(currColor != preColor)
                {
                    currCount ++
                    preColor = currColor
                }
            else
                {
                    currCount = 1
                    preColor = currColor
                }
            if(currCount >=k)
                {
                    rs++
                }
        }
    return rs


};
