class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n = len(colors)
        pos = []
        for i in range(n):
            if colors[i] == colors[(i + 1) % n]:
                pos.append(i)

        if len(pos) == 0: return n
        pos.append(pos[0] + n)
        ans = 0
        for x, y in pairwise(pos):
            ans += max(y - x - k + 1, 0)
        return ans
