class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& arr, int k) {
        int n=arr.size();
        vector<int> a(n+k-1);
        for(int i=0;i<n;i++)
        {
            a[i]=arr[i];
        }
        for(int i=n;i<n+k-1;i++)
        {
            a[i]=arr[i%n];
        }
        int i=0;int j=0;
        int expected;
        int len=0;
        int ans=0;
        while(j<a.size())
        {
            if(j==0)
            {
                expected=1-a[j];
                j++;
                len++;
                continue;
            }
            if(a[j]==expected)
            {
                len++;
                expected=1-expected;
            }
            else
            {
                len=1;
                expected=1-a[j];
            }
            if(j-i+1<k)
            {
                j++;
            }
            else if(j-i+1==k)
            {
                if(len>=k)
                {
                    ans++;
                }
                j++;
                i++;
            }
        }
        return ans;
    }
};
