class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        int n=colors.length;
        int[] nc=new int[n+k];
        for (int i=0;i<n;i++){
            nc[i]=colors[i];
        }
        for (int i=n;i<n+k-1;i++){
            nc[i]=colors[i-n];
        }
        int last=-1;
        for (int i=0;i<k-1;i++){
            if (nc[i]==nc[i+1]){
                last=i;
            }
        }
        int cou=0;
        for (int i=k-1;i<n+k-1;i++){
          //  System.out.println(i+" "+last);
            if (i-last>=k){
                cou++;
            }
            if (nc[i]==nc[i+1]){
                last=i;
            }
        }
        return cou;
    }
}
