class Solution:
    def numberOfAlternatingGroups(self, arr: List[int], k: int) -> int:
        i, j, res = 0, 1, 0

        while j < len(arr) + k and i < len(arr):
            j2 = j
            temp = j2 % len(arr)  
            while arr[temp] != arr[temp - 1] and (j2 < (len(arr) + k - 1)):
                j2 += 1
                temp = j2 % len(arr)

            res += max(j2 - i - k + 1, 0)
            i, j = j2, i+1

        return res
