class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();
        if(k>n) return 0;

        vector<int>a=colors;
        a.insert(a.end(),colors.begin(),colors.end());
        vector<int>pt1(k),pt2(k);
        for(int i=0;i<k;i++){
            pt1[i]=i%2;      
            pt2[i]=(i+1)%2;  
        }
        int ptt=0,pts=0,b=0;
        int base=2,mod=1e9+7; 

        for (int i=0;i<k;i++){
            ptt=(ptt*base+pt1[i])%mod;
            pts=(pts*base + pt2[i])%mod;
            b=(b*base+a[i])%mod;
        }

        int ct=0;
        if (b==ptt || b==pts){
            ct++;
        }
        int it = 1;
        for(int i=1;i<k;i++){
            it=(it*base)%mod;
        }
        for(int i=1;i<n;i++){
            b=((b-a[i-1]*it%mod + mod) % mod*base + a[i+k-1])%mod;
            if (b==ptt or b==pts){
                ct++;
            }
        }
        return ct;
    }
};
