class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        for (int i=0;i<k-1;i++)
            colors.push_back(colors[i]);
        int i=0,j=0;
        int ans = 0;
        n = colors.size();
        // for (auto &elem:colors)
        //         cout<<elem<<" ";
        // cout<<"\n";
        while(i<n&&j<n)
        {
            if (j-i+1>=2&&colors[j-1]==colors[j])
            {
                //cout<<i<<" "<<j<<"\n";
                i = j;
            }
            else if (j-i+1<k) j++;
            else
            {
                ans++;
                j++;
                i++;
            }
        }
        return ans;
    }
};
