class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        vector<int> sfx(n);
        sfx[n-1] = 1;
        for(int i= n-2;i>=0;i--)
        {
           if(colors[i]!=colors[i+1]) sfx[i] = 1+sfx[i+1];
           else sfx[i] = 1;
        }
        vector<int> pfx(n);
        pfx[0] = 1;
        for(int i = 1;i<n;i++){
            if(colors[i]!=colors[i-1]) pfx[i] = 1 + pfx[i-1];
            else pfx[i] = 1;
}
        int ans = 0;
        for(int i = 0;i<n;i++){
            if(sfx[i]>=k) ans++;
            else if(i+k-1>=n && colors[n-1]!=colors[0]){
                if(sfx[i]==n-i && pfx[k-(n-i)-1]==k-(n-i)) ans++;
            }

        }
        return ans;

    }
};
