class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& cc, int k) {
        int ans=0;
int n=cc.size();
vector<int> c=cc;
for(auto i:cc){
    c.push_back(i);
}
        for(int i=0;i<cc.size();){
            int j=i+1;
            while(j<=(n+k-2) && c[j]!=c[j-1]  ){
                j++;
            }

            int tot = j-i;
            if(tot>=k){
                ans+=(tot-k+1);
            }

            i=j;
        }

        return ans;
    }
};
