class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();

        int count = 0;

        int len = 1;
        int i = 1;
        while(i<n+k-1){
            int curr = i;
            int prev = i-1;

            if(curr >= n){
                curr = curr - n;
            }
            if(prev >= n){
                prev = prev - n;
            }

            if(colors[curr]!=colors[prev]){
                len++;
            }
            else{
                len = 1;
            }

            if(len >= k){
                count++;
            }

            i++;
        }
        return count;
    }
};
