class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        vector<int> v;
        for (int i = 0; i < colors.size(); i++) {
            v.push_back(colors[i]);
        }
        for (int i = 0; i < colors.size(); i++) {
            v.push_back(colors[i]);
        }
        int cnt = 1;
        int ans = 0;
        for (int i = 1; i < colors.size() + k - 1; i++) {
            if (v[i] != v[i - 1]) {
                cnt++;
            } else {
                cnt = 1;
            }
            if (cnt >= k) {
                ans++;
            }
        }
        return ans;
    }
};
