class Solution {
public:
    int dp[200010][2];
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int n=a.size();
        for(int i=0;i<n;++i)
            a.push_back(a[i]);
        a.insert(a.begin(),0);
        int res=0;
        for(int i=1;i<=n*2;++i) {
            dp[i][a[i]]=dp[i-1][a[i]^1]+1;
            if(dp[i][a[i]]>=k&&i-k+1<=n) ++res;
        }
        return res;
    }
};
