class Solution {
public:
    int countSubarraysOfOnes(vector<int>& binaryArray, int k) {
        int n = binaryArray.size();
        int count1 = 0;
        int count0=0;
        for(int i=0;i<k;i++){
            if(binaryArray[i]==1){
                count1++;
            }
            else{
                count0++;
            }

        }
        int i=k;
        int ans=(count0==0);
        int j=0;
        while(i<n){
            if(binaryArray[j]==0)count0--;
            else count1--;

            if(binaryArray[i]==0)count0++;
            else count1++;
            ans+=(count0==0);
            i++;
            j++;

        }
        return ans;




}

    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        vector<int> temp=colors;
        int n=colors.size();

        for(int i=0;i<k-1;i++){
            temp.push_back(colors[i]);
        }
        int sum=0;
        vector<int> z;

        for(int i=0;i<temp.size()-1;i++){
            z.push_back(temp[i]^temp[i+1]);
        }
        for(int i=0;i<k-1;i++){
            if(z[i]==1) sum++;
        }
        int ans=0;
        int i=0,j=k-1;
        while(j<z.size()){
            if(sum==k-1) ans++;
            sum-=z[i];
            sum+=z[j];
            i++;
            j++;
        }
        if(sum==k-1) ans++;
        return ans;



    }
};
