class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size(); 
        int sankhya = 0; 

        if (k == 1) { 
             return n; 
        } 

        int motapa = 1; 
        int col = 1; 
        while (col < n + k - 1) { 
            int tatkal = col; 
            int pichla = col - 1; 


            if (tatkal >= n) { 
                tatkal -= n; 
            } 
            if (pichla >= n) { 
                pichla -= n; 
            } 

            if (colors[tatkal] != colors[pichla]) { 
                ++motapa; 
            } else { 
                motapa = 1; 
            } 


            if (motapa >= k) { 
                ++sankhya; 
            } 

            ++col; 
        } 

        return sankhya; 
    }
};
