class Solution {
public:
    int calc(int x,vector<int>&prediff){
        int n=prediff.size();
        if(x<n) return prediff[x];
        else return prediff[x%n]+prediff[n-1];
    }
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();
        vector<bool> diff(n,0);
        for(int i=0;i<n;i++) if(colors[i]!=colors[(i-1+n)%n] && colors[i]!=colors[(i+1)%n]) diff[i]=true;
        vector<int> prediff(n,0);
        prediff[0]=diff[0];
        for(int i=1;i<n;i++) prediff[i]=prediff[i-1]+diff[i];
        int ans=0;
        for(int i=0;i<n;i++){
            if(calc(i+k-1,prediff)-calc(i+1,prediff)==k-2) ans++;
        }
        return ans;
    }
};
