class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {

        for(int i=0;i<k-1;++i)
        {
            c.push_back(c[i]);
        }
        int i=0;
        int j=1;
        int ans=0;
        while(j<c.size())
        {
            int st=c[i];
            while(j<c.size() && st!=c[j])
            {
                st=1-st;
                j++;
            }
            ans+=max(0,j-i-k+1);
            i=j;
            j++;
        }

        return ans;



    }
};
