constexpr int MAX = 200010;

int c[MAX], sum[MAX];

class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
      int n = colors.size();
      for (int i = 0; i < n; ++i) {
        c[i] = colors[i];
        c[i + n] = colors[i];
      }
      sum[0] = 0;
      for (int i = 1; i < 2 * n; ++i) {
        sum[i] = sum[i - 1] + (c[i] != c[i - 1]);
      }
      int ret = 0;
      for (int i = 0; i < n; ++i) {
        if (sum[i + k - 1] - sum[i] == k - 1) {
          ++ret;
        }
      }
      return ret;
    }
};
